            variant="contained" 
            color="secondary" 
            size="large"
            sx={{ 
              color: 'white',
              borderColor: 'white',
              px: 4,
              py: 1.5,
              backgroundColor: 'rgba(255, 255, 255, 0.2)',
              '&:hover': {
                backgroundColor: 'rgba(255, 255, 255, 0.3)',
              }
            }}
          >
            Let's Connect
          </Button>
        </Container>
      </Box>
    </Box>
  );
};

export default Home;
